mapper.select:
SELECT mac_activity.activity_type as activity, mac.`name`, mac.area_code, mac.IMEI, mac.pos, mac.`status`, mac.id, mac.address
FROM food_machine AS mac LEFT JOIN food_machine_activity_ref AS mac_activity ON mac_activity.mac_id = mac.id
WHERE mac.id = #{macId}

SELECT product.id AS productId, product.NAME AS productName, product.show_price AS showPrice, product.give_score AS giveScore, product.period AS period, product.remind AS remind, product.picture AS picture, product.total_stock AS totalStock, product.unit AS unit, product.zs_weight AS zsWeight, product.cp_weight AS cpWeight, product.warm_flag AS isWarm, product.warm_time AS warmTime, product.hot_num AS hotNum, product.label AS label, product. STATUS, product.up_time AS upTime, product.down_time AS downTime, product.descr, product.is_meal as isMeal, product.type as type, ais.price, ais.stock, mac.cat_id AS catId, cat.NAME AS catName FROM food_machine AS mac LEFT JOIN food_machine_aisle AS ais ON mac.id = ais.mac_id LEFT JOIN food_product AS product ON product.id = ais.product_id LEFT JOIN food_machine_product_cat AS cat ON mac.id = cat.mac_id WHERE mac.id = #{macId}
and product.product_cat_id=#{catId}
and limit #{pageNo}, #{pageSize};

select mac.id AS macId, mac.`name`, mac.`address`, mac.IMEI AS macCode, mac.area_code as areaCode, mac.notice, mac.cat_id AS catId,
aisle.id as aisleId, aisle.stock as stock, aisle.price, aisle.product_cat_id as productCatId,
product.id as productId,product.name as productName,product.materials as materials, product.show_price as showPrice, product.picture as picture, product.unit as unit, product.zs_weight as zsWeight, product.cp_weight as cpWeight, product.warm_flag as warmFlag, product.warm_time as warmTime, product.hot_num as hotNum, product.label as label, product.`status` as status, product.up_time as upTime, date_add(product.up_time, interval period day) as downTime, product.keyword as keyword, product.descr as descr, product.type as type, product.create_time as createTime from food_machine_aisle as aisle left join food_product as product on product.id = aisle.product_id left join food_machine as mac on mac.id = aisle.mac_id where product.keyword like CONCAT(CONCAT('%',#{keyword},'%'));
